$(function(){$("select").on("change",()=>{let t=$("select").val();$.getJSON(`https://api.nytimes.com/svc/topstories/v2/${t}.json?&max-results=5&api-key=hbvVpmOEMRq8xxJSav79k8nlLeNRfUGg`).done(function(t){console.log("data",t);let e=[];$.each(t.results,function(t,n){n.multimedia.length>=1&&e.push(n)});for(let t=0;t<12;t++){let n=e[t].title,l=e[t].abstract;e[t].multimedia[2].url;$("main").append(`<div><h2>${n}</h2><p>${l}</p></div>`)}}).fail(function(){}).always(function(){})})});