$(function(){$("select").on("change",()=>{let t=$("select").val();$.getJSON(`https://api.nytimes.com/svc/topstories/v2/${t}.json?&max-results=5&api-key=hbvVpmOEMRq8xxJSav79k8nlLeNRfUGg`).done(function(t){console.log("data",t);let e=[];$.each(t.results,function(t,l){l.multimedia.length>=1&&e.push(l)}),console.log(e);for(let t=0;t<12;t++){let l=e[t].title,s=e[t].abstract,a=e[t].multimedia[4].url,i=e[t].url;$("main").append(`<div class="top-story"><img src="${a}"><div class="text"><a href="${i}"><h2>${l}</h2></a><p>${s}</p></div></div>`)}}).fail(function(){}).always(function(){})})});